	submit: function (type, form) {
		var sendSummary = type !== 'subscription' && type !== 'subscription_en' && type !== 'review';

		if (typeof type === 'undefined')
			type = 'callback';

		var eventType = type;
		var formValid = true;
		var formReplace = $(form).find(".message_in");
		var data = $(form).serializeArray();
		var action = type == 'review' ? 'submit-review':'submit-form';
		var formData = {};
		$(form).addClass("ng-submitted");
		$(form).find ('input, textarea, select').each(function() {
		  formValid=(ui.checkReq(this)==false)?false:formValid;
		  formData[this.name] = $(this).val();
		});

		if (formValid) {
			if(type === 'order_request')
				eventType = 'order_request_' + formData.pageId.split('.')[0] + '_' + formData.variant;
			if(type === 'price_request')
				eventType = formData.eventName;

			formData.formType = type;
			data[data.length]={"name":"formType","value":type};
			formData.sessionId = window.call_value;
			data[data.length]={"name":"sessionId","value":formData.sessionId};

			$.ajax({
				url: "/local/ajax",
				type: "POST",
				data: $.param({action: action, data: formData}),
				dataType: "json",
				success: function (responce) {
					if(responce.status == true) {
						if(window.yaCounter34853365)
						{
							yaCounter34853365.reachGoal(eventType);

							if(sendSummary)
								yaCounter34853365.reachGoal('summary_order');
						}

						if(window.ga)
						{
							window.ga('send', 'event', 'form', eventType);

							if (sendSummary)
								window.ga('send', 'event', 'form', 'summary_order');
						}
						var curpage = location.pathname,
						curpage_en =(curpage.split('/').indexOf('en')>-1)? true:false;

						if(formReplace.length && responce["message"]){
							$(formReplace).html('<h3 class="h3">'+responce["message"]+'</h3>');
						}
						else if(type == 'subscription'){
							location.href="/thanks?subcribe="+$('#subscribe').val();
						}
						else if(curpage_en){
							location.href="/en/thanks";
						}
						else
							location.href="/thanks";
					} else if(responce.status == false) {
						$(form).append('<h3 class="h3">'+responce["message"]+'</h3>');
					}
				}
			});
		}
		return false;
	},
	checkReq: function (elem) {
		var elemValid = true;
		var form = elem.form;
		  if($(elem).attr("name")=="privacy"){
			var label = $(elem).parent('label');
			if($(elem).prop("checked"))
			{

				label.removeClass('checkbox-error error-animate');
			}
			else {
				elemValid = false;
				setTimeout(function () {
					label.addClass('checkbox-error error-animate');
				},300);
			}
		  }
		  else if ($(elem).val()==""){
			$(elem).addClass('ng-empty').removeClass('ng-not-empty');
		  } else {
			$(elem).addClass('ng-not-empty').removeClass('ng-empty');
		  }
		if($(form).hasClass("ng-submitted")){
		  if ($(elem).data("required-any")){
			elemValid = false;  
			$(form).find('input[data-required-any='+$(elem).data("required-any")+']').each(function() {
				if(elemValid == true)
					return;
			    elemValid=(this.checkValidity())?true:false;
			});
			if (elemValid){
				$(form).find('input[data-required-any='+$(elem).data("required-any")+']').addClass('ng-valid ng-valid-required ng-untouched').removeClass('ng-invalid ng-invalid-required ng-touched');
			  } else {
				$(form).find('input[data-required-any='+$(elem).data("required-any")+']').addClass('ng-invalid ng-invalid-required ng-touched').removeClass('ng-valid ng-valid-required ng-untouched');
			  }
		  } else if (elem.checkValidity()){
			$(elem).addClass('ng-valid ng-valid-required ng-untouched').removeClass('ng-invalid ng-invalid-required ng-touched');
		  } else {
			$(elem).addClass('ng-invalid ng-invalid-required ng-touched').removeClass('ng-valid ng-valid-required ng-untouched');
			elemValid = false;  
		  }
		}
		return elemValid;
	},
