  favorites: function(obj) {
	var favs={"blog":[],"portfolio":[]};
	if($.cookie('favorites')){
		favs=JSON.parse($.cookie('favorites'));
		if(favs[obj].length){
			for (var key in favs[obj]) 
				$("[data-element-id="+favs[obj][key]+"]").find("a").addClass('active').html('  <span class="arr"></span>');
		}
		//$(".favorites-block > span").text(favscount);
	}
    $('data-favorite-button a').click( function(e){
		if($(this).hasClass('active')){
			location.href="/favorites";
		}
		else{
		  e.preventDefault();
		  $(this).addClass('active');
		  if ($(this).closest('data-favorite-button').length>0) {
			var title,
			$id=$(this).closest('data-favorite-button').attr("data-element-id");
			$(this).hasClass('active') ? title= '  <span class="arr"></span>' :  title= '   <span class="arr"></span>';
			$(this).html(title);
			if($(this).hasClass('active')){
				if($.inArray($id, favs[obj]==-1))
					favs[obj][favs[obj].length]=$id;
			}
			else{
				if($.inArray($id, favs[obj])>=0){
					for (var key in favs) {
						if (favs[obj][key] == $id) {
							favs[obj].splice(key, 1);
						}
					}
				}
			}
			$.cookie('favorites', JSON.stringify(favs), {expires: 150, path: '/',});
		  }
		}
    });

  }
