		url  = 'https://pinterest.com/pin/create/button/?';
		url += 'url='          + encodeURIComponent(purl);
		url += '&description=' + encodeURIComponent(ptitle);
		url += '&media='    + encodeURIComponent(pimg);
		Share.popup(url);
